chrome.runtime.onInstalled.addListener(()=>{chrome.contextMenus.create({id:"check-font",title:"Check Font for selection",contexts:["selection"]})});chrome.contextMenus.onClicked.addListener((t,e)=>{t.menuItemId==="check-font"&&(e!=null&&e.id)&&chrome.tabs.sendMessage(e.id,{action:"showOverlay",selectionText:t.selectionText}).catch(()=>{console.warn("Could not send message to content script. Is it loaded?")})});
